mapper.select:
SELECT c.*, 
group_concat( CONCAT(b.product_id, ',' , b.quantity) ) 
AS productId_quantity 
FROM command c 
LEFT JOIN basket b 
ON b.command_id = c.id

     c.status = #{status} 
    AND c.returned = #{returned} 
     AND c.date > #{date_gte} 
     AND c.date  <  #{date_lte} 
     AND c.total_ex_taxes > #{total_gte} 
     AND c.customer_id = #{customer_id} 

GROUP BY c.id 
ORDER BY c.${sort} ${order} 
LIMIT #{take} OFFSET #{start}

SELECT COUNT(c.id) FROM command c

     c.status = #{status} 
    AND c.returned = #{returned} 
     AND c.date > #{date_gte} 
     AND c.date  <  #{date_lte} 
     AND c.total_ex_taxes > #{total_gte} 
     AND c.customer_id = #{customer_id}

UPDATE command 


        status = #{status},


        returned = #{returned}


WHERE id = #{id}
