mapper.select:
SELECT * FROM product

    category_id = #{category_id}
    AND sales > #{sales_gt} 
    AND sales  <  #{sales_lte} 
    AND stock > #{stock_gt} 
    AND stock  <  #{stock_lt} 

        AND description like '%${searchText}%'
        OR reference like '%${searchText}%'


ORDER BY ${sort} ${order}
LIMIT #{take}
OFFSET #{start}

SELECT count(*) FROM product

    category_id = #{category_id}
     AND sales > #{sales_gt} 
     AND sales  <  #{sales_lte} 
    AND stock > #{stock_gt} 
     AND stock  <  #{stock_lt} 

        AND description like '%${searchText}%'
        OR reference like '%${searchText}%'
